    private void cbkodeMotorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbkodeMotorActionPerformed
        // TODO add your handling code here:
        String kodeMotor,namaMotor;
        int hargaMotor;

        kodeMotor = String.valueOf(cbkodeMotor.getSelectedItem());

        if (kodeMotor.equals("A01")){
            namaMotor="Honda Vario 150";
            hargaMotor=24000000;
        }else
            if (kodeMotor.equals("A02")){
            namaMotor="Yamaha Aerox 155";
            hargaMotor=28000000;
        }else
                 if (kodeMotor.equals("A03")){
                namaMotor="Suzuki GSX-R 150";
                hargaMotor=29000000;
        }else
                    if (kodeMotor.equals("A04")){
                    namaMotor="Kawasaki Ninja 250 SE";
                    hargaMotor=66000000;
        }else
        {               
                        namaMotor="Honda CBR 250RR SP";
                        hargaMotor=77000000;
        }
        txtnamaMotor.setText(""+namaMotor);
        txthargaMotor.setText(""+hargaMotor);
        

    }//GEN-LAST:event_cbkodeMotorActionPerformed

