    private void btndiskoNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndiskoNActionPerformed
        // TODO add your handling code here:
        int hargaMotor, diskoN;
        int uniT;

        uniT = Integer.parseInt(txtuniT.getText());
        hargaMotor = Integer.parseInt(txthargaMotor.getText());

        if(uniT > 1) {
            diskoN = (int) (hargaMotor * 0.20);
        }else
        {
            diskoN = 0;
        }
        txtdiskoN.setText(""+diskoN);

    }//GEN-LAST:event_btndiskoNActionPerformed

    private void btntotaLActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntotaLActionPerformed
        // TODO add your handling code here:
        int hargaMotor,diskoN,totBayar;
        int uniT;

        hargaMotor = Integer.parseInt(txthargaMotor.getText());
        uniT = Integer.parseInt(txtuniT.getText());
        diskoN = Integer.parseInt(txtdiskoN.getText());

        totBayar = (hargaMotor * uniT)-diskoN;
        txttotBayar.setText(""+totBayar);
    }//GEN-LAST:event_btntotaLActionPerformed

    private void btnuangBayarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnuangBayarActionPerformed
        // TODO add your handling code here:
        int totBayar, uangBayar,uangKembalian;
        
        totBayar = Integer.parseInt(txttotBayar.getText());
        uangBayar = Integer.parseInt(txtuangBayar.getText());
        
        if (uangBayar > totBayar){
            uangKembalian = uangBayar - totBayar;
            txtuangKembalian.setText(""+uangKembalian);
        }else
        { txtuangKembalian.setText("Yah duid nya kurang :( " );
        
        }

    }//GEN-LAST:event_btnuangBayarActionPerformed

